mapper.select:
SELECT result1.* FROM (SELECT sr_num,user_id,sr_title,sr_content,viewCount, TO_CHAR(sr_regdate,'YY.MM.DD HH24:MI') AS regdate, LAG(sr_num,1,0) OVER (ORDER BY sr_num DESC) AS prevNum, LEAD(sr_num,1,0) OVER (ORDER BY sr_num DESC) AS nextNum FROM seller_review
WHERE sr_title LIKE '%'||#{sr_title}||'%' OR sr_content LIKE '%'||#{sr_content}||'%'
WHERE sr_title LIKE '%'||#{sr_title}||'%'
WHERE user_id LIKE '%'||#{user_id}||'%'
) result1 WHERE sr_num=#{sr_num}

mapper.update:
UPDATE seller_review
SET viewCount=viewCount+1
WHERE sr_num=#{sr_num}

UPDATE seller_review
SET sr_title=#{sr_title}, sr_content=#{sr_content}
WHERE sr_num=#{sr_num}
